import React from 'react'
import GridItem from '../GridItem'
import * as C from './styles'

const Grid = ({items, setItems}) => {

    function onDelete(id){
        const newArray = items.filter((transaction) => transaction.id !== id);
        setItems(newArray);
        localStorage.setItem('transactions', JSON.stringify(newArray));
    }

  return (
    <C.Table>
        <C.Thead>
            <C.Tr>
                <C.Th width={40}>Description</C.Th>
            </C.Tr>
            <C.Tr>
                <C.Th width={40}>Value</C.Th>
            </C.Tr>
            <C.Tr>
                <C.Th width={10} alignCenter>Type</C.Th>
            </C.Tr>
            <C.Tr>
                <C.Th width={10}>#</C.Th>
            </C.Tr>
        </C.Thead>
        <C.Tbody>
            {items?.map((item, index) => {
                <GridItem key={index} item={item} onDelete={onDelete}></GridItem>
            })}
        </C.Tbody>
    </C.Table>
  )
}

export default Grid
